importtkinter#导入tkinter模块fromtkinterimport*fromtkinterimportttk,messagebox,colorchooserroot=tkinter.Tk()root.minsize(280,500)root.title('Calculator')#1.界面布局#显示面板result=tkinter.StringVar()result.set(0)#显示面板显示结果1，用于显示默认数字0result2=tkinter.StringVar()#显示面板显示结果2，用于显示计算过程result2.set('')#显示版label=tkinter.Label(root,font=('consolas',20),bg='#FFFFFF',bd='9',fg='#828282',anchor='se',textvariable=result2)label.place(width=280,height=170)label2=tkinter.Label(root,font=('consolas',30),bg='#FFFFFF',bd='9',fg='black',anchor='se',textvariable=result)label2.place(y=170,width=280,height=60)#数字键按钮btn7=tkinter.Button(root,text='7',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('7'))btn7.place(x=0,y=285,width=70,height=55)btn8=tkinter.Button(root,text='8',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('8'))btn8.place(x=70,y=285,width=70,height=55)btn9=tkinter.Button(root,text='9',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('9'))btn9.place(x=140,y=285,width=70,height=55)btn4=tkinter.Button(root,text='4',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('4'))btn4.place(x=0,y=340,width=70,height=55)btn5=tkinter.Button(root,text='5',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('5'))btn5.place(x=70,y=340,width=70,height=55)btn6=tkinter.Button(root,text='6',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('6'))btn6.place(x=140,y=340,width=70,height=55)btn1=tkinter.Button(root,text='1',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('1'))btn1.place(x=0,y=395,width=70,height=55)btn2=tkinter.Button(root,text='2',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('2'))btn2.place(x=70,y=395,width=70,height=55)btn3=tkinter.Button(root,text='3',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('3'))btn3.place(x=140,y=395,width=70,height=55)btn0=tkinter.Button(root,text='0',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('0'))btn0.place(x=70,y=450,width=70,height=55)#运算符号按钮btnac=tkinter.Button(root,text='AC',bd=0.5,font=('consolas',20),fg='orange',command=lambda:pressCompute('AC'))btnac.place(x=0,y=230,width=70,height=55)btnback=tkinter.Button(root,text='←',font=('consolas',20),fg='#4F4F4F',bd=0.5,command=lambda:pressCompute('b'))btnback.place(x=70,y=230,width=70,height=55)btndivi=tkinter.Button(root,text='÷',font=('consolas',20),fg='#4F4F4F',bd=0.5,command=lambda:pressCompute('/'))btndivi.place(x=140,y=230,width=70,height=55)btnmul=tkinter.Button(root,text='×',font=('consolas',20),fg="#4F4F4F",bd=0.5,command=lambda:pressCompute('*'))btnmul.place(x=210,y=230,width=70,height=55)btnsub=tkinter.Button(root,text='-',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressCompute('-'))btnsub.place(x=210,y=285,width=70,height=55)btnadd=tkinter.Button(root,text='+',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressCompute('+'))btnadd.place(x=210,y=340,width=70,height=55)btnequ=tkinter.Button(root,text='=',bg='orange',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressEqual())btnequ.place(x=210,y=395,width=70,height=110)btnper=tkinter.Button(root,text='%',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressCompute('%'))btnper.place(x=0,y=450,width=70,height=55)btnpoint=tkinter.Button(root,text='.',font=('consolas',20),fg=('#4F4F4F'),bd=0.5,command=lambda:pressNum('.'))btnpoint.place(x=140,y=450,width=70,height=55)frame=Frame(root)frame.pack(fill=X)exit=tkinter.Button(frame,text='×',font=('consolas'),fg=('#000000'),bg=('#FF0000'),bd=0.5,command=lambda:root.destroy())exit.pack(side=RIGHT)information=tkinter.Button(frame,text='i',font=('consolas'),fg=('#000000'),bd=0.5,command=lambda:information('名字','身份证号','年龄','3.6.5'))information.pack(side=LEFT)hexf=tkinter.Button(frame,text='Hex',font=('consolas'),fg=('#000000'),bd=0.5,command=lambda:Hex(result.get()))hexf.pack(side=LEFT)binf=tkinter.Button(frame,text='Bin',font=('consolas'),fg=('#000000'),bd=0.5,command=lambda:Bin(result.get()))binf.pack(side=LEFT)octf=tkinter.Button(frame,text='Oct',font=('consolas'),fg=('#000000'),bd=0.5,command=lambda:Oct(result.get()))octf.pack(side=LEFT)#操作函数lists=[]#设置一个变量保存运算数字和符号的列表isPressSign=False#添加一个判断是否按下运算符号的标志,假设默认没有按下按钮isPressNum=FalsedefHex(num):try:result.set(hex(int(num)))result2.set('hex({0})'.format(num))except:messagebox.showwarning('Error','InvalidSyntax')defBin(num):try:result.set(bin(int(num)))result2.set('bin({0})'.format(num))except:messagebox.showwarning('Error','InvalidSyntax')defOct(num):try:result.set(oct(int(num)))result2.set('oct({0})'.format(num))except:messagebox.showwarning('Error','InvalidSyntax')#信息函数definformation(Author,ID,Age,Version):messagebox.showinfo('Information','Author:{0}
ID:{1}
Age:{2}
Version:{3}'.format(Author,ID,Age,Version))#数字函数defpressNum(num):#设置一个数字函数判断是否按下数字并获取数字将数字写在显示版上globallists#全局化lists和按钮状态isPressSignglobalisPressSignifisPressSign==False:passelse:#重新将运算符号状态设置为否result.set(0)isPressSign=False#判断界面的数字是否为0ifnum!='.':oldnum=result.get()#第一步ifoldnum=='0':#如过界面上数字为0则获取按下的数字result.set(num)else:#如果界面上的而数字不是0则链接上新按下的数字newnum=oldnum+numresult.set(newnum)#将按下的数字写到面板中else:oldnum=result.get()if'.'inoldnum:passelse:newnum=oldnum+numresult.set(newnum)#运算函数defpressCompute(sign):globallistsglobalisPressSignnum=result.get()#获取界面数字lists.append(num)#保存界面获取的数字到列表中lists.append(sign)#讲按下的运算符号保存到列表中isPressSign=Trueifsign=='AC':#如果按下的是'AC'按键，则清空列表内容，讲屏幕上的数字键设置为默认数字0lists.clear()result.set(0)result2.set('')ifsign=='b':#如果按下的是退格‘'，则选取当前数字第一位到倒数第二位a=num[0:-1]lists.clear()result.set(a)#获取运算结果函数defpressEqual():globallistsglobalisPressSigncurnum=result.get()#设置当前数字变量，并获取添加到列表lists.append(curnum)try:computrStr=''.join(lists)#讲列表内容用join命令将字符串链接起来endNum=eval(computrStr.replace('=',''))#用eval命令运算字符串中的内容a=str(endNum)b='='+a#c=b[0:10]ifendNum%1!=0:endNum=eval('{:.6f}'.format(endNum))result.set('='+str(endNum))#讲运算结果显示到屏幕1result2.set(str(computrStr).replace('=≈',''))#将运算过程显示到屏幕2lists.clear()#清空列表内容else:result.set('='+str(endNum))#讲运算结果显示到屏幕1result2.set(str(computrStr).replace('=≈',''))#将运算过程显示到屏幕2lists.clear()exceptZeroDivisionError:messagebox.showerror('Error','ZeroDivisionError')result.set('0')result2.set('')exceptSyntaxError:messagebox.showerror('Error','SyntaxError')result.set('0')result2.set('')root.mainloop()
